mapper.select:
select * from menu 
where ccode=#{ccode}
order by menu desc

mapper.update:
update menu set menu=#{menu},price=#{price},image=#{image}
where
ccode=#{ccode} and menu=#{menu}

mapper.select:
		select count(*) from menu

			where ccode=#{ccode} and menu like '%' || #{keyword} || '%'



 		where ccode=#{ccode} and price <= #{keyword}




 		where ccode=#{ccode} and price >= #{keyword}
